package Conversions;

public class DataTypeconvert {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int d = 67;
		int a =(int) d;
		char ch=(char)d;
		long l= (long)d;
		System.out.println("int="+a);
		System.out.println("char ="+ch);
		System.out.println("long="+l);
		
		

	}

}
