package collections;

public class Unboxing {    // UNBOXING

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    
	       Integer ref = new Integer(23);
	       System.out.println("integer="+ref);
	      int a=ref;
	      System.out.println("int ="+a);
	       Character c1=new Character('a');
	       System.out.println("char="+c1);
	       Character c2=c1;
	       System.out.println("char="+c2);
	       
	       Boolean  fe=new Boolean(false);
	       System.out.println("float="+fe);
	       
	       //  java object to primitive value is known as UN-Boxing operations

	}

}
